import css from './index.less';
import { Button } from 'antd';

const conf = [
    {
        text: "新增",
        type: "primary",
        shape: 'round',
    },
    {
        text: "编辑",
    },
    {
        text: "上传",
    }
]
export default () => {
    return <div className={css.ToolBar}>
        {conf.map((el, idx) => {
            const { text, type, shape } = el;
            return <Button type={type} style={{marginRight:5}} shape>
                {text}
            </Button>
        })}
    </div>
}
