import css from './index.less';
import { Button } from 'antd';

const conf = [
    {
        text: "新增",
        type: "primary",
        shape: 'round',
        marginRight:8,
    },
    {
        text: "编辑",
    },
    {
        text: "上传",
    }
]
export default () => {
    return <div className={css.ToolBar}>
        {conf.map((el, idx) => {
            const { text, type, shape,marginRight } = el;
            return <Button type={type} style={{marginRight: marginRight|| 5}} shape={shape}>
                {text}
            </Button>
        })}
    </div>
}
